mapper.update:
UPDATE member
SET login_locked = #{loginLocked}, login_lock_time = #{loginLockTime}
WHERE member_id = #{memberId}

UPDATE member
SET refresh_token = #{refreshToken}
WHERE id = #{id}

mapper.select:
SELECT member_id
FROM member
WHERE email = #{email} AND member_name = #{memberName}

SELECT COUNT(*)
FROM member
WHERE email = #{email}

SELECT COUNT(*)
FROM member
WHERE member_id = #{memberId}
